function burger(){return function(){const e=document.body,t={nav:nav=document.querySelector(".nav"),burgerButton:burgerButton=document.querySelector(".burger-button")},c={blocked:blocked="_blocked",active:active="_active"};t.burgerButton.addEventListener("click",()=>{e.classList.toggle(c.blocked),t.nav.classList.toggle(c.active),t.burgerButton.classList.toggle(c.active)})}}burger()();