document,window,document.querySelectorAll('a[href*="#"]').forEach(e=>{e.addEventListener("click",o=>{o.preventDefault();const t=e.getAttribute("href");if(""!==t)if("#"===t)window.scrollTo({top:0,behavior:"smooth"});else{const e=document.querySelector(""+t);e&&e.scrollIntoView({behavior:"smooth"})}})});